package day2;

import java.util.ArrayList;

/**
 * Created by nathan.lamb on 12/2/2016.
 */
public class BathroomCode {

    public static void main(String args[]) {
        TerribleKeyPad kp = new TerribleKeyPad();

        String directions = "\n" +
                "\n" +
                "\n" +
                "\n" +
                "\n";

        String[] codes = directions.split("\n");

        ArrayList<Character> keyCode = new ArrayList<Character>();

        System.out.println("Initialized, at pos: " + kp.currentKey());

        for(String s: codes) {
            char code = ' ';

            char[] directionCharArray = s.toCharArray();

            for (int i = 0; i < directionCharArray.length; i++) {
                switch (directionCharArray[i]) {
                    case 'U':
                        code = kp.moveUp();
                        break;
                    case 'D':
                        code = kp.moveDown();
                        break;
                    case 'R':
                        code = kp.moveRight();
                        break;
                    case 'L':
                        code = kp.moveLeft();
                        break;
                }
            }

            System.out.println("Next code digit is: " + code);
            keyCode.add(code);
        }

        System.out.println("Key code should be: ");
        for (Character key: keyCode) {
            System.out.print(key);
        }
    }
}
